<script>
function scaleLongString(selecter, maxHeight) {
    $(selecter).each(function() {
        $(this).text($(this).attr("title"));
        if (($(this).height() > maxHeight)) {
            if (!$(this).attr("title")) {
                $(this).attr("title", $(this).text());
            }
            var wordArr = $(this).attr("title").split("");
            while (($(this).height() > maxHeight) && wordArr.length) {
                wordArr.pop();
                $(this).text(wordArr.join("") + "...");
            }
            $(this).attr("data-text", $(this).text());
            $(this).mouseover(function() {
                $(this).css("background-color", "#fff").css("height", "auto").css("margin-left", "-5px").css("border-left", "5px solid #fff").css("min-height", "40px").css("display", "block");
                $(this).parent().css("overflow", "reset");
                $(this).text($(this).attr("title"));
            }).mouseleave(function() {
                $(this).removeAttr("style");
                $(this).text($(this).attr("data-text"));
            });
        } else {
            $(this).off("mouseover").off("mouseleave").removeAttr("data-text").removeAttr("title");
        }
    })
}
